import React from 'react';
import { TodoCounter } from '../TodoCounter';
import { TodoSearch } from '../TodoSearch';
import { TodoList } from '../TodoList';
import { TodoItem } from '../TodoItem';
import { CreateTodoButton } from '../CreateTodoButton';
import { useLocalStorage } from './useLocalSorage';

/*
const defaultTodos = [
  { text: 'Cortar cebolla', completed: true },
  { text: 'Tomar el Curso de Intro a React.js', completed: false },
  { text: 'Llorar con la Llorona', completed: false },
  { text: 'LALALALALA', completed: false },
  { text: 'Manejar React', completed: true },
]; 

localStorage.setItem("OBLI_V1", JSON.stringify (defaultTodos));  

 localStorage.removeItem("OBLI_V1");  */


function App() {
const [todos, saveTodos] = useLocalStorage("OBLI_V1", []);
const [searchValue, setSearchValue] = React.useState("");

const completedTodos = todos.filter
(todo => !!todo.completed).length;

const totalTodos = todos.length;

const searchedTodos = todos.filter(
(todo) => {
  const todoText = todo.text.toLowerCase();
  const searchText = searchValue.toLowerCase();
  return todoText.includes(searchText);
}
);


const completeTodo = (text) => {
  const newTodos = [...todos];
  const todoIndex = newTodos.findIndex(
    (todo) => todo.text === text 
    );
  newTodos[todoIndex].completed = true;
  saveTodos(newTodos);
  };

const deleteTodo = (text) => {
  const newTodos = [...todos];
  const todoIndex = newTodos.findIndex(
    (todo) => todo.text === text 
    );
  newTodos.splice (todoIndex, 1);
  saveTodos(newTodos);
};
}

export default App;
